<?phpclass C_Page extends C_Base{	//	// �����������.	//		public function action_index(){		$this->title .= '::index';        $d= M_Pdo::instance();        $res=$d->Select("SELECT * FROM articles");		$this->content = $this->Template('v/v_index.php', array('text' => $res));	}		public function action_edit(){		$this->title .= '::��������������';				if($this->isPost())		{			text_set($_POST['text']);			header('location: index.php');			exit();		}				$text = text_get();		$this->content = $this->Template('v/v_edit.php', array('text' => $text));			}}